def sql_dependentes(execucaoPrincipal):
    """Funcao que faz a busca dos dependentes de um responsável específico"""

    # Query SQL para buscar as matrículas
    sql = f"""
                select DISTINCT
                    sistema = 300,
                    bethadba.dbf_get_id_gerado(sistema, 'pessoa', f.i_pessoas) as id_pessoa,
                    bethadba.dbf_get_id_gerado(sistema, 'pessoa', df.i_dependentes) as id_dependente,
                    CAST(pf.dt_nascimento AS VARCHAR(255)) as data_nascimento,
                    pDep.nome as nome_dependente,
                    CASE d.grau
					    WHEN 1 THEN 'FILHO'
					    WHEN 2 THEN 'CONJUGE'
					    WHEN 3 THEN 'PAI_MAE'
					    WHEN 4 THEN 'AVO'
					    WHEN 5 THEN 'IRMAO'
					    WHEN 6 THEN 'NETO'
					    WHEN 7 THEN 'ENTEADO'
					    WHEN 8 THEN 'MENOR_TUTELADO'
					    WHEN 9 THEN 'COMPANHEIRO'
					    WHEN 11 THEN 'BISNETO'
					    WHEN 12 THEN 'BISAVO'
					    ELSE 'OUTROS'  -- Caso o valor seja 10 ou nulo
					END AS grau,
					CAST(d.dt_ini_depende AS VARCHAR(255)) as data_inicio,
                    CASE df.dep_irrf 
                    	WHEN 'S' THEN 'True'
                    	ELSE 'False'
                    END AS irrf,
                    CASE Df.i_tipos_salfam 
                    	WHEN 5 THEN 'False'
                    	ELSE 'True'
                    END AS salario_familia
                from bethadba.dependentes_func df
                join bethadba.funcionarios f on f.i_funcionarios = df.i_funcionarios and f.i_entidades = df.i_entidades
                join bethadba.dependentes d on d.i_dependentes = df.i_dependentes and d.i_pessoas = f.i_pessoas
                join bethadba.pessoas_fisicas pf on df.i_dependentes = pf.i_pessoas
                join bethadba.pessoas pDep on df.i_dependentes = pDep.i_pessoas
                where f.i_funcionarios in ({execucaoPrincipal.matriculas})
    """
    return sql